  attachmentsLog.push(attachment)
}
driver.on('MESSAGE_SENTTOBOT', listenerMe)
driver.on('MESSAGE_RECEIVEDFROMBOT', listenerBot)
driver.on('MESSAGE_ATTACHMENT', listenerAttachments)
